SELECT BUM.NAME_BUM as 'businessUnit',RCT.CODE_RCT as 'employeeCode',RCT.NAME_RCT AS 'employeeName',RCT.ADDRESS_RCT AS 'address', RCT.CITY_RCT AS 'city',ULV.NAME_ULV AS 'designation',RCT.STATE_RCT AS 'state',RCT.ZIP_RCT AS 'zip',RCT.ZONE_RCT AS 'zone',RCT.LoginId AS 'loginId', RCT.WorkId AS 'workId',RCT.Gender AS 'gender',RCT.JOINING_DATE_RCT AS 'joiningDate',RCT.MOBILE_RCT AS 'mobile',RCT.EMAIL_RCT AS 'email', TEM.NAME_TEM AS 'team',RCT.NSM_NAME_RCT AS 'nsmName',RCT.NSM_CODE_RCT AS 'nsmCode',RCT.RM_NAME_RCT AS 'rmName',RCT.RM_CODE_RCT AS 'rmCode', rct.EMAIL_RM AS 'emailRM',rct.EMAIL_AM AS 'emailAM',BUM.NAME_BUM AS 'businessUnit', RCT.AM_NAME_RCT AS 'amName',RCT.AM_CODE_RCT AS 'amCode',RCT.CFA_RCT AS 'cfa',RCT.HEAD_QUARTER_RCT AS 'hq',SLV.NAME_SLV AS 'status' FROM RECIPIENT_MASTER_RCT RCT JOIN TEAM_MASTER_TEM TEM ON TEM.ID_TEM = RCT.ID_TEM_RCT JOIN USER_LOV_ULV ULV ON ULV.ID_ULV = RCT.ID_DESG_ULV_RCT --join DIVISION_MASTER_DIM dim on dim.ID_DIM = tem.ID_DIM_TEM JOIN BU_MASTER_BUM BUM ON BUM.ID_BUM = RCT.ID_BUM_RCT join SYSTEM_LOV_SLV slv on slv.ID_SLV = RCT.ID_STATUS_SLV_RCT Where RCT.ID_STATUS_SLV_RCT = #{statusId} and TEM.ID_TEM in #{item}
and BUM.ID_BUM in #{item}
order by RCT.JOINING_DATE_RCT desc

